mapper.select:
select count(*)
from member
where m_status != 3

	and dept_id = #{selectDept}


	and job_id = #{selectJob}


	and m_name like '%' || #{searchValue} || '%'

SELECT M_ID, M_PWD, M_NAME, M_BIRTH_DATE, M_EMAIL, M_PHONE, M_ADDRESS, M_PROFILE, M_IMG_ORIGIN_NAME, M_IMG_CHANGE_NAME, M_ENROLL_DATE, M_UPDATE_DATE, M_HIRE_DATE, M_END_DATE, M_STATUS, M_MANAGER_YN, JOB_NAME, DEPT_ID, DEPT_NAME FROM MEMBER LEFT JOIN DEPARTMENT USING(DEPT_ID) LEFT JOIN JOB USING(JOB_ID) WHERE M_STATUS != 3
and dept_id = #{selectDept}
and job_id = #{selectJob}
and m_name like '%' || #{searchValue} || '%'
ORDER BY M_STATUS, M_ENROLL_DATE DESC

mapper.update:
update member
set m_status = 3
where m_id = #{mId}

update job
set job_status = 1
where job_id = #{jobId}

update job
set job_name = #{jobName}, job_order = #{jobOrder}
where job_id = #{jobId}
