mapper.select:

SELECT COUNT(*)
FROM BOARD
	LEFT JOIN MEMBER USING(M_ID)

	B_STATUS = '0'
	AND B_TYPE = '자유'



			AND B_TITLE LIKE #{value}


			AND B_CONTENT LIKE #{value}


			AND (B_TITLE LIKE #{value}
				OR B_CONTENT LIKE #{value})

SELECT B_NUM, B_TYPE, B_TITLE, B_VIEWS, B_CREARE_DATE, B_MODIFY_DATE, B_STATUS, B_PIN, B.M_ID M_ID FROM BOARD B LEFT JOIN MEMBER M ON(B.M_ID = M.M_ID)
B_STATUS = '0' AND B_TYPE = '자유'
AND B_TITLE LIKE #{value}
AND B_CONTENT LIKE #{value}
AND (B_TITLE LIKE #{value} OR B_CONTENT LIKE #{value})
ORDER BY B_NUM DESC

mapper.update:
UPDATE BOARD 
SET B_TITLE = #{bTitle}, B_CONTENT = #{bContent}, B_MODIFY_DATE = SYSDATE
WHERE B_NUM = #{bId}

UPDATE 
REPLY SET REPLY_STATUS = '1' 
WHERE B_NUM = #{refBid} AND REPLY_NO = #{rId}
