mapper.select:
select count(*)
from meet_reservation
where m_id = #{mId}

mapper.update:

update meet_reservation
set rev_status = '1'
where rev_end_time <= #{tNow}

mapper.select:
select count(*) from meet_reservation where m_id = #{mId}
and rev_no like '%' || #{searchKeyword} || '%'
and rev_date = #{searchKeyword}
and rev_status = #{searchKeyword}
and meet_no in (select meet_no from meeting_room where meet_name like '%' || #{searchKeyword} || '%')
and m_id in (select m_id from member where m_name = #{searchKeyword})
and rev_content like '%' || #{searchKeyword} || '%'

select rev_no, rev_date, rev_start_time, rev_end_time, rev_content, rev_status, rev_apply_date, meet_no, m_id, meet_name, m_name, m_email, job_name, dept_name from meet_reservation left join member using(m_id) left join meeting_room using(meet_no) left join job using(job_id) left join department using(dept_id) where m_id = #{mId}
and rev_no like '%' || #{searchKeyword} || '%'
and rev_date = #{searchKeyword}
and rev_status = #{searchKeyword}
and meet_no in (select meet_no from meeting_room where meet_name like '%' || #{searchKeyword} || '%')
and m_id in (select m_id from member where m_name = #{searchKeyword})
and rev_content like '%' || #{searchKeyword} || '%'
order by rev_date desc, rev_start_time

mapper.update:
update meet_reservation
set rev_date = #{rev_date}, rev_start_time = #{rev_start_time}, rev_end_time = #{rev_end_time},
    rev_content = #{rev_content}, rev_apply_date = sysdate, meet_no = #{meet_no}, m_id = #{mId}
where rev_no = #{rev_no}

update meet_reservation
set rev_status = '1'
where rev_no = #{rNo}

update meet_reservation
set rev_status = '2'
where rev_no = #{rNo}
