mapper.update:
update board
set b_views = b_views + 1
where b_num = #{bNum}

mapper.select:
select count(*)
from scrap
where b_Num = #{bNum} and m_Id = #{mId}

mapper.update:
update reply
set reply_content = #{replyContent},
	reply_modify_date = #{replyModifyDate}
where reply_no = #{replyNo}

update reply
set reply_status = 1
where reply_no = #{replyNo}

update board
set b_status = 1
where b_num = #{bNum}

update attachment
set file_status = 1
where file_no = #{fileNo}

update board
set b_title = #{bTitle},
	b_content = #{bContent},
	b_pin = #{pin},
	b_modify_date = #{modifyDate}
where  b_num = #{bNum}
